public interface IEntityManager extends Serializable {
	public <T> Collection<T> getEntitiesByQuery(String aQueryName, final Class<T> aDaoClass);
	public <T> Collection<T> getEntitiesByQuery(IQuery aQuery, final Class<T> aDaoClass);
	public <T> Collection<T> getEntitiesByQuery(String aQueryName, IDatabase aDatabase, final Class<T> aDaoClass);
	public <T> Collection<T> getEntitiesByQuery(IQuery aQuery, IDatabase aDatabase, final Class<T> aDaoClass);
	public <T> T getEntity(String aDocumentId, final Class<T> aDaoClass);
	public <T> T getEntity(String aDocumentId, IDatabase aDatabase, final Class<T> aDaoClass);
	public <T> T getEntity(final IDocument aDocument, final Class<T> aDaoClass);	
	public <T> void setEntity(T aDao);	
	public <T> void setEntity(T aDao, IDatabase aDatabase);	
	public <T> void setEntity(T aDao, IDocument aDocument);
}
